#!/bin/bash

# Azure Blob Storage Setup Script
# Run this script to create storage account and get connection string

RESOURCE_GROUP="snapfest-rg"
STORAGE_ACCOUNT_NAME="snapfeststorage"
LOCATION="eastus"
CONTAINER_NAME="uploads"

echo "🚀 Setting up Azure Blob Storage..."
echo ""

# Create storage account
echo "📦 Creating storage account..."
az storage account create \
    --name $STORAGE_ACCOUNT_NAME \
    --resource-group $RESOURCE_GROUP \
    --location $LOCATION \
    --sku Standard_LRS \
    --kind StorageV2 \
    --allow-blob-public-access true

if [ $? -eq 0 ]; then
    echo "✅ Storage account created"
else
    echo "⚠️  Storage account may already exist or there's a subscription issue"
fi

# Wait a bit
sleep 15

# Get connection string
echo ""
echo "🔑 Getting connection string..."
CONNECTION_STRING=$(az storage account show-connection-string \
    --name $STORAGE_ACCOUNT_NAME \
    --resource-group $RESOURCE_GROUP \
    --query connectionString \
    --output tsv)

# Create container
echo "📁 Creating container..."
az storage container create \
    --name $CONTAINER_NAME \
    --account-name $STORAGE_ACCOUNT_NAME \
    --public-access blob \
    --connection-string "$CONNECTION_STRING" 2>/dev/null || echo "Container may already exist"

